







// Joption pane input from day 3

import javax.swing.*;
public class Input2Day3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String name = JOptionPane.showInputDialog("Enter your name please! :D");
		System.out.println(name);

		int age = Integer.parseInt(JOptionPane.showInputDialog("Enter your age please! :D"));
        System.out.println(age);


		System.out.println("Your name is " + name +" and your age is " + age); 
        
    }

		
	

}


